(1286, '後尖山步道(支線)', '中', '南投縣魚池鄉中山路599號', '以玄光寺碼頭為起點，向上方延伸經過玄光寺後可抵達玄奘寺。', '886-49-2855668', '2812', '2', NULL, NULL, '120.8955592', '23.83060602', '120.895562', '23.830586', '120.895591', '23.830526', '120.895964', '23.829982', '120.895919', '23.828345', '南投縣', NULL),
(1287, '水社大山登山步道(支線)', '中', '南投縣魚池鄉中山路599號', '海拔2059公尺，全線水平上升達1300公尺，極具挑戰性，往返需約8小時。', '886-49-2855668', '708', '5', 'http://www.sunmoonlake.gov.tw/PhotoView.aspx?KeyID=ce1c5017-2675-4f19-b246-a4c54b7f57aa&SubPIndex=12#', NULL, '120.9436538', '23.8499021', '120.9437641', '23.8499945', '6120.94406943', '3.85009158120', '0.94420982', '3.8501789820', '20.944433823', '3.85028227；12', '南投縣', NULL),
(1288, '水社親水步道2(支線)', '中', '南投縣魚池鄉中山路599號', '由水社至文武廟，沿途湖光山色秀麗絕倫。', '886-49-2855668', '10', '2', 'http://www.sunmoonlake.gov.tw/SceneryView.aspx?KeyID=9c1caac5-d3bd-4ae8-9e56-865aee211f57', NULL, '120.9194128', '23.8720621', '120.9194504', '23.87207859', '7866120.9195453', '323.87207183', '120.919702', '23.87204742', '120.9199748', '，23.87200981；', '南投縣', NULL),
(1289, '水濂橋步道(支線)', '北', '新竹縣峨眉鄉七星村六寮60-8號', '由遊客中心大門出口右轉可到水濂洞(梵音寺)，其為一座天然拱形洞。', '886-3-5800689', '240', '1', 'http://www.trimt-nsa.gov.tw/web/FileDownLoad/Scenery/BigPicture/20160224095337636.jpg', 'http://www.trimt-nsa.gov.tw/web/FileDownLoad/Scenery/BigPicture/20160224094921323.jpg', '121.0264353', '24.6549019', '121.0263271', '24.65495188', '5093121.0262428', '24.65493403', '121.0258488', '824.65488644', '121.0258017', '，24.65503564；', '新竹縣', NULL),
